mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY B.NREF DESC, B.STEP ASC) RNUM, B.BOARD_IDX, B.SUBMENU_ID, B.BOARD_TITLE, B.BOARD_CONT, B.USERCODE, TO_CHAR(B.BOARD_REGDATE, 'YYYY-MM-DD HH24:MI:SS') BOARD_REGDATE, B.READCOUNT, B.BNUM, B.LVL, B.STEP, B.NREF, B.DELBOARD, B.PARENT, M.NICKNAME FROM BOARD B JOIN MEMBER M ON B.USERCODE = M.USERCODE
WHERE SUBMENU_ID = #{submenu_id}
) T WHERE T.RNUM BETWEEN #{startnum} AND #{endnum}

mapper.update:

UPDATE
	BOARD
SET
	STEP    =  STEP + 1
WHERE
	SUBMENU_ID =  #{ submenu_id }
AND
    NREF    =  #{ nref } 
AND
    step    >  #{ step }

insert.foreach:
INTO  BOARD_FILES  VALUES (
     GET_FILENUM()
   , ( SELECT MAX(BOARD_IDX) FROM BOARD  )
   , #{ file.filename   }
   , #{ file.fileext    }
   , #{ file.sfilename  }
)

mapper.update:

UPDATE   BOARD
 SET     READCOUNT = READCOUNT + 1
 WHERE   BOARD_IDX   =   #{board_idx}

insert.foreach:
INTO  BOARD_FILES  VALUES (
     GET_FILENUM()
   , #{ board_idx       }
   , #{ file.filename   }
   , #{ file.fileext    }
   , #{ file.sfilename  }
)
